import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [status, setStatus] = useState("Pending");
  const [editId, setEditId] = useState(null);

  // Fetch tasks
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get("/api/tasks");
      setTasks(res.data);
    } catch (err) {
      console.error("❌ Could not fetch tasks:", err.message);
    }
  };

  // Add or Update Task
  const saveTask = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;

    try {
      if (editId) {
        // Update existing task
        await axios.put(`/api/tasks/${editId}`, { title, status });
        setEditId(null);
      } else {
        // Add new task
        await axios.post("/api/tasks", { title, status });
      }

      setTitle("");
      setStatus("Pending");
      fetchTasks(); // refresh tasks
    } catch (err) {
      console.error("❌ Error saving task:", err.message);
    }
  };

  // Delete task
  const deleteTask = async (id) => {
    if (!window.confirm("Delete this task?")) return;

    try {
      await axios.delete(`/api/tasks/${id}`);
      fetchTasks();
    } catch (err) {
      console.error("❌ Error deleting task:", err.message);
    }
  };

  // Edit task
  const editTask = (task) => {
    setTitle(task.title);
    setStatus(task.status);
    setEditId(task._id);
  };

  // Change status quickly
  const updateStatus = async (task, newStatus) => {
    try {
      await axios.put(`/api/tasks/${task._id}`, { ...task, status: newStatus });
      fetchTasks();
    } catch (err) {
      console.error("❌ Error updating status:", err.message);
    }
  };

  return (
    <div style={{ margin: "30px" }}>
      <h1>📝 Task Manager</h1>

      {/* Add / Edit Form */}
      <form onSubmit={saveTask} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Enter task title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ padding: "8px", marginRight: "10px" }}
        />
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          style={{ padding: "8px", marginRight: "10px" }}
        >
          <option value="Pending">Pending</option>
          <option value="In-progress">In-progress</option>
          <option value="Done">Done</option>
        </select>
        <button type="submit" style={{ padding: "8px 15px" }}>
          {editId ? "✏️ Update Task" : "➕ Add Task"}
        </button>
        {editId && (
          <button
            type="button"
            onClick={() => {
              setEditId(null);
              setTitle("");
              setStatus("Pending");
            }}
            style={{ padding: "8px 15px", marginLeft: "10px" }}
          >
            ❌ Cancel
          </button>
        )}
      </form>

      {/* Task List */}
      <ul style={{ listStyle: "none", padding: 0 }}>
        {tasks.length > 0 ? (
          tasks.map((task) => (
            <li
              key={task._id}
              style={{
                display: "flex",
                justifyContent: "space-between",
                alignItems: "center",
                padding: "10px",
                borderBottom: "1px solid #ccc",
              }}
            >
              <div>
                <b>{task.title}</b> - {task.status}
              </div>
              <div style={{ display: "flex", gap: "10px" }}>
                <button onClick={() => editTask(task)}>✏️ Edit</button>
                <button onClick={() => updateStatus(task, "Pending")}>🕓 Pending</button>
                <button onClick={() => updateStatus(task, "In-progress")}>⚡ In-progress</button>
                <button onClick={() => updateStatus(task, "Done")}>✅ Done</button>
                <button
                  onClick={() => deleteTask(task._id)}
                  style={{ color: "#fff", background: "#e74c3c", border: "none", borderRadius: 4, padding: "4px 8px" }}
                >
                  🗑️ Delete
                </button>
              </div>
            </li>
          ))
        ) : (
          <p>No tasks yet.</p>
        )}
      </ul>
    </div>
  );
}

export default App;
