import { useEffect, useState } from "react";
import axios from "axios";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [status, setStatus] = useState("Pending");

  // Fetch tasks
  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const res = await axios.get("/api/tasks");
      setTasks(res.data);
    } catch (err) {
      console.error("‚ùå Could not fetch tasks:", err.message);
    }
  };

  // Add new task
  const addTask = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;

    try {
      const res = await axios.post("/api/tasks", { title, status });
      setTasks([...tasks, res.data]); // update UI immediately
      setTitle(""); // clear input
      setStatus("Pending");
    } catch (err) {
      console.error("‚ùå Error adding task:", err.message);
    }
  };

  return (
    <div style={{ margin: "30px" }}>
      <h1>üìù My Tasks</h1>

      {/* Add Task Form */}
      <form onSubmit={addTask} style={{ marginBottom: "20px" }}>
        <input
          type="text"
          placeholder="Enter task title"
          value={title}
          onChange={(e) => setTitle(e.target.value)}
          style={{ padding: "8px", marginRight: "10px" }}
        />
        <select
          value={status}
          onChange={(e) => setStatus(e.target.value)}
          style={{ padding: "8px", marginRight: "10px" }}
        >
          <option value="Pending">Pending</option>
          <option value="In-progress">In-progress</option>
          <option value="Done">Done</option>
        </select>
        <button type="submit" style={{ padding: "8px 15px" }}>
          ‚ûï Add Task
        </button>
      </form>

      {/* Task List */}
      <ul>
        {tasks.length > 0 ? (
          tasks.map((task) => (
            <li key={task._id}>
              {task.title} - <b>{task.status}</b>
            </li>
          ))
        ) : (
          <p>No tasks yet.</p>
        )}
      </ul>
    </div>
  );
}

export default App;
