import axios from "axios";
import { useEffect, useState } from "react";

function App() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [editId, setEditId] = useState(null);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    setLoading(true);
    try {
      const res = await axios.get("/api/tasks");
      setTasks(res.data);
    } catch (err) {
      console.error("Error fetching tasks:", err);
      alert("Could not fetch tasks. Is the backend running on http://localhost:5000 ?");
    } finally {
      setLoading(false);
    }
  };

  const saveTask = async () => {
    if (!title.trim()) return alert("Please enter a title");
    try {
      if (editId) {
        await axios.put(`/api/tasks/${editId}`, { title });
        setEditId(null);
      } else {
        await axios.post("/api/tasks", { title });
      }
      setTitle("");
      fetchTasks();
    } catch (err) {
      console.error("Error saving task:", err);
      alert("Error saving task");
    }
  };

  const updateStatus = async (id, status) => {
    try {
      await axios.put(`/api/tasks/${id}`, { status });
      fetchTasks();
    } catch (err) {
      console.error("Error updating status:", err);
    }
  };

  const deleteTask = async (id) => {
    if (!window.confirm("Delete this task?")) return;
    try {
      await axios.delete(`/api/tasks/${id}`);
      fetchTasks();
    } catch (err) {
      console.error("Error deleting task:", err);
    }
  };

  const editTask = (task) => {
    setTitle(task.title);
    setEditId(task._id);
    window.scrollTo({ top: 0, behavior: "smooth" });
  };

  return (
    <div className="container">
      <div className="card">
        <h1>Task Manager</h1>
        <div style={{ display: "flex", gap: 8, marginTop: 8 }}>
          <input
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            placeholder="Enter task title"
            style={{ flex: 1, padding: "8px 10px", borderRadius: 6, border: "1px solid #d1d5db" }}
          />
          <button
            onClick={saveTask}
            style={{ background: "#111827", color: "#fff", padding: "8px 12px", borderRadius: 6, border: "none" }}
          >
            {editId ? "Update" : "Add"}
          </button>
          {editId && (
            <button
              onClick={() => { setEditId(null); setTitle(""); }}
              style={{ padding: "8px 12px", borderRadius: 6, border: "1px solid #d1d5db", background: "#fff" }}
            >
              Cancel
            </button>
          )}
        </div>
      </div>

      <div style={{ height: 16 }} />

      <div className="card">
        <div style={{ display: "flex", justifyContent: "space-between", alignItems: "center" }}>
          <h2>Tasks</h2>
          <button onClick={fetchTasks} style={{ padding: "6px 10px", borderRadius: 6 }}>Refresh</button>
        </div>

        {loading && <div style={{ padding: 20 }}>Loading...</div>}

        {!loading && tasks.length === 0 && <div style={{ padding: 20 }}>No tasks yet.</div>}

        <ul style={{ listStyle: "none", padding: 0, marginTop: 12 }}>
          {tasks.map((task) => (
            <li key={task._id} style={{ display: "flex", justifyContent: "space-between", gap: 12, alignItems: "center", padding: 12, borderBottom: "1px solid #f3f4f6" }}>
              <div>
                <div style={{ fontWeight: 600 }}>{task.title}</div>
                <div style={{ fontSize: 13, color: "#6b7280" }}>{task.status}</div>
              </div>

              <div style={{ display: "flex", gap: 8 }}>
                <button onClick={() => editTask(task)} style={{ padding: "6px 8px" }}>Edit</button>
                <button onClick={() => updateStatus(task._id, "In-progress")} style={{ padding: "6px 8px" }}>In-progress</button>
                <button onClick={() => updateStatus(task._id, "Done")} style={{ padding: "6px 8px" }}>Done</button>
                <button onClick={() => deleteTask(task._id)} style={{ padding: "6px 8px", color: "#fff", background: "#ef4444", border: "none", borderRadius: 6 }}>Delete</button>
              </div>
            </li>
          ))}
        </ul>
      </div>
    </div>
  );
}

export default App;
