import { useEffect, useState } from "react";
import axios from "axios";

function TaskPage() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [status, setStatus] = useState("Pending");
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("/api/tasks", {
        headers: { Authorization: `Bearer ${token}` },
      });
      setTasks(res.data);
    } catch (err) {
      console.error("❌ Could not fetch tasks:", err.message);
    }
  };

  const saveTask = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;
    try {
      const token = localStorage.getItem("token");
      if (editId) {
        await axios.put(
          `/api/tasks/${editId}`,
          { title, status },
          { headers: { Authorization: `Bearer ${token}` } }
        );
        setEditId(null);
      } else {
        await axios.post(
          "/api/tasks",
          { title, status },
          { headers: { Authorization: `Bearer ${token}` } }
        );
      }
      setTitle("");
      setStatus("Pending");
      fetchTasks();
    } catch (err) {
      console.error("❌ Error saving task:", err.message);
    }
  };

  const deleteTask = async (id) => {
    if (!window.confirm("Delete this task?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`/api/tasks/${id}`, {
        headers: { Authorization: `Bearer ${token}` },
      });
      fetchTasks();
    } catch (err) {
      console.error("❌ Error deleting task:", err.message);
    }
  };

  const editTask = (task) => {
    setTitle(task.title);
    setStatus(task.status);
    setEditId(task._id);
  };

  const updateStatus = async (task, newStatus) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(
        `/api/tasks/${task._id}`,
        { ...task, status: newStatus },
        { headers: { Authorization: `Bearer ${token}` } }
      );
      fetchTasks();
    } catch (err) {
      console.error("❌ Error updating status:", err.message);
    }
  };

  return (
    <div className="min-h-screen bg-gray-100 p-6">
      <div className="max-w-3xl mx-auto bg-white shadow-lg rounded-2xl p-6">
        <h1 className="text-3xl font-bold text-center text-indigo-600 mb-6">
          📝 Task Manager
        </h1>

        {/* Add/Edit Task Form */}
        <form
          onSubmit={saveTask}
          className="flex flex-col md:flex-row gap-3 mb-6"
        >
          <input
            type="text"
            placeholder="Enter task title"
            value={title}
            onChange={(e) => setTitle(e.target.value)}
            className="flex-1 px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          />
          <select
            value={status}
            onChange={(e) => setStatus(e.target.value)}
            className="px-4 py-2 border rounded-lg focus:outline-none focus:ring-2 focus:ring-indigo-500"
          >
            <option value="Pending">Pending</option>
            <option value="In-progress">In-progress</option>
            <option value="Done">Done</option>
          </select>
          <button
            type="submit"
            className="bg-indigo-600 text-white px-4 py-2 rounded-lg hover:bg-indigo-700 transition"
          >
            {editId ? "✏️ Update" : "➕ Add"}
          </button>
          {editId && (
            <button
              type="button"
              onClick={() => {
                setEditId(null);
                setTitle("");
                setStatus("Pending");
              }}
              className="bg-gray-400 text-white px-4 py-2 rounded-lg hover:bg-gray-500 transition"
            >
              ❌ Cancel
            </button>
          )}
        </form>

        {/* Task List */}
        {tasks.length > 0 ? (
          <ul className="space-y-3">
            {tasks.map((task) => (
              <li
                key={task._id}
                className="flex justify-between items-center bg-gray-50 border rounded-lg p-4 shadow-sm"
              >
                <div>
                  <p className="font-semibold text-gray-800">{task.title}</p>
                  <p
                    className={`text-sm ${
                      task.status === "Done"
                        ? "text-green-600"
                        : task.status === "In-progress"
                        ? "text-yellow-600"
                        : "text-gray-500"
                    }`}
                  >
                    {task.status}
                  </p>
                </div>
                <div className="flex gap-2">
                  <button
                    onClick={() => editTask(task)}
                    className="px-3 py-1 text-sm bg-blue-500 text-white rounded-lg hover:bg-blue-600"
                  >
                    ✏️ Edit
                  </button>
                  <button
                    onClick={() => updateStatus(task, "Pending")}
                    className="px-3 py-1 text-sm bg-gray-300 rounded-lg hover:bg-gray-400"
                  >
                    🕓
                  </button>
                  <button
                    onClick={() => updateStatus(task, "In-progress")}
                    className="px-3 py-1 text-sm bg-yellow-400 text-white rounded-lg hover:bg-yellow-500"
                  >
                    ⚡
                  </button>
                  <button
                    onClick={() => updateStatus(task, "Done")}
                    className="px-3 py-1 text-sm bg-green-500 text-white rounded-lg hover:bg-green-600"
                  >
                    ✅
                  </button>
                  <button
                    onClick={() => deleteTask(task._id)}
                    className="px-3 py-1 text-sm bg-red-500 text-white rounded-lg hover:bg-red-600"
                  >
                    🗑️
                  </button>
                </div>
              </li>
            ))}
          </ul>
        ) : (
          <p className="text-center text-gray-500 mt-6">No tasks yet.</p>
        )}
      </div>
    </div>
  );
}

export default TaskPage;
