import { useEffect, useState } from "react";
import axios from "axios";

function TaskPage() {
  const [tasks, setTasks] = useState([]);
  const [title, setTitle] = useState("");
  const [status, setStatus] = useState("Pending");
  const [editId, setEditId] = useState(null);

  useEffect(() => {
    fetchTasks();
  }, []);

  const fetchTasks = async () => {
    try {
      const token = localStorage.getItem("token");
      const res = await axios.get("/api/tasks", { headers: { Authorization: `Bearer ${token}` } });
      setTasks(res.data);
    } catch (err) {
      console.error("❌ Could not fetch tasks:", err.message);
    }
  };

  const saveTask = async (e) => {
    e.preventDefault();
    if (!title.trim()) return;
    try {
      const token = localStorage.getItem("token");
      if (editId) {
        await axios.put(`/api/tasks/${editId}`, { title, status }, { headers: { Authorization: `Bearer ${token}` } });
        setEditId(null);
      } else {
        await axios.post("/api/tasks", { title, status }, { headers: { Authorization: `Bearer ${token}` } });
      }
      setTitle("");
      setStatus("Pending");
      fetchTasks();
    } catch (err) {
      console.error("❌ Error saving task:", err.message);
    }
  };

  const deleteTask = async (id) => {
    if (!window.confirm("Delete this task?")) return;
    try {
      const token = localStorage.getItem("token");
      await axios.delete(`/api/tasks/${id}`, { headers: { Authorization: `Bearer ${token}` } });
      fetchTasks();
    } catch (err) {
      console.error("❌ Error deleting task:", err.message);
    }
  };

  const editTask = (task) => {
    setTitle(task.title);
    setStatus(task.status);
    setEditId(task._id);
  };

  const updateStatus = async (task, newStatus) => {
    try {
      const token = localStorage.getItem("token");
      await axios.put(`/api/tasks/${task._id}`, { ...task, status: newStatus }, { headers: { Authorization: `Bearer ${token}` } });
      fetchTasks();
    } catch (err) {
      console.error("❌ Error updating status:", err.message);
    }
  };

  return (
    <div style={{ margin: "30px" }}>
      <h1>📝 Task Manager</h1>

      <form onSubmit={saveTask} style={{ marginBottom: "20px" }}>
        <input type="text" placeholder="Enter task title" value={title} onChange={(e) => setTitle(e.target.value)} style={{ padding: "8px", marginRight: "10px" }} />
        <select value={status} onChange={(e) => setStatus(e.target.value)} style={{ padding: "8px", marginRight: "10px" }}>
          <option value="Pending">Pending</option>
          <option value="In-progress">In-progress</option>
          <option value="Done">Done</option>
        </select>
        <button type="submit">{editId ? "✏️ Update Task" : "➕ Add Task"}</button>
        {editId && <button type="button" onClick={() => { setEditId(null); setTitle(""); setStatus("Pending"); }}>❌ Cancel</button>}
      </form>

      <ul style={{ listStyle: "none", padding: 0 }}>
        {tasks.length > 0 ? tasks.map((task) => (
          <li key={task._id} style={{ display: "flex", justifyContent: "space-between", alignItems: "center", padding: "10px", borderBottom: "1px solid #ccc" }}>
            <div><b>{task.title}</b> - {task.status}</div>
            <div style={{ display: "flex", gap: "10px" }}>
              <button onClick={() => editTask(task)}>✏️ Edit</button>
              <button onClick={() => updateStatus(task, "Pending")}>🕓 Pending</button>
              <button onClick={() => updateStatus(task, "In-progress")}>⚡ In-progress</button>
              <button onClick={() => updateStatus(task, "Done")}>✅ Done</button>
              <button onClick={() => deleteTask(task._id)} style={{ color: "#fff", background: "#e74c3c", border: "none", borderRadius: 4, padding: "4px 8px" }}>🗑️ Delete</button>
            </div>
          </li>
        )) : <p>No tasks yet.</p>}
      </ul>
    </div>
  );
}

export default TaskPage;
